import { REMOVE_ALERT, SET_ALERT } from "./types";
import { createAction } from "redux-actions";

export const alertActionCreator = {
  set_alert: createAction(SET_ALERT),
  remove_alert: createAction(REMOVE_ALERT),
};
function setAlert(params) {
  return {
    type: SET_ALERT,
    payload:params,
  };
}
function removeAlert(params) {
  return {
    type: REMOVE_ALERT,
    payload:params,
  };
}
