import { REMOVE_ALERT, SET_ALERT } from "./types";
// import { createAction } from "redux-actions";

// const alertActionCreator = {
//   setAlert: createAction(SET_ALERT),
//   removeAlert: createAction(REMOVE_ALERT),
// };
// export default alertActionCreator;

export const set_alert = (params) => {
  const id = Math.floor(Math.random() * 100000);
  /// before return we have set a timeout to clear alerts
  setTimeout(remove_alert(id), 5000);
  return {
    type: SET_ALERT,
    payload: {
      id,
      msg: params.msg,
      alertType: params.alertType,
    },
  };
};

export const remove_alert = (id) => (dispatch) => {
  dispatch({
    type: REMOVE_ALERT,
    payload: id,
  });
};
