import {
  GET_ALL_POST,
  GET_ALL_POST_ERROR,
  GET_ALL_POST_SUCCESS,
} from "../action/types";

const initialState = {
  posts: [],
  post: null,
  loading: false,
  error: {},
};
export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case GET_ALL_POST: {
      return {
        ...state,
        loading: true,
      };
    }
    case GET_ALL_POST_SUCCESS: {
      return {
        ...state,
        loading: false,
        posts: payload,
      };
    }
    case GET_ALL_POST_ERROR: {
      return {
        ...state,
        loading: false,
      };
    }
    default: {
      return state;
    }
  }
}
