import { remove_alert, set_alert } from "../action/alert";

export const AlertHelper = (msg, alertType, time) => (dispatch) => {
  const id = Math.floor(Math.random() * 100000);
  dispatch(
    set_alert({
      id: id,
      msg: msg,
      alertType: alertType,
    })
  );
  setTimeout(() => {
    remove_alert(id);
  }, time);
};
