import React, { Fragment, useEffect, useState } from "react";
import PropTypes from "prop-types";
import { globalConnect } from "../../redux/connect/globalConnect";
import Moment from "react-moment";
import { Link } from "react-router-dom";
import Spinner from "../../components/layouts/spinner";

const PostDetails = (props) => {
  const [formData, setFormData] = useState({
    text: "",
  });

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const submitForm = async (e) => {
    e.preventDefault();
    if (password !== password2) {
      AlertHelper({ msg: "Password do not Matched", alertType: "danger" });
    } else {
      const User = {
        name,
        email,
        password,
      };
      try {
        props.registerUser({
          body: User,
        });
      } catch (error) {
        console.log(error);
      }
    }
  };
  useEffect(() => {
    props.getSinglePost({
      postId: props.match?.params?.id,
    });
  }, []);
  const { post } = props;
  return (
    <Fragment>
      {post.loading ? (
        <Spinner></Spinner>
      ) : post.post !== null ? (
        <Fragment>
          <Link to="/Posts" class="btn">
            Back To Posts
          </Link>
          <div class="post bg-white p-1 my-1">
            <div>
              <Link to={`profile/${post?.post?.user}`}>
                <img class="round-img" src={post?.post?.avatar} alt="" />
                <h4>{post?.post?.name}</h4>
              </Link>
            </div>
            <div>
              <p class="my-1">{post?.post?.text}</p>
            </div>
          </div>

          <div class="post-form">
            <div class="bg-primary p">
              <h3>Leave A Comment</h3>
            </div>
            <form class="form my-1" onSubmit={(e) => submitForm(e)}>
              <textarea
                name="text"
                cols="30"
                rows="5"
                placeholder="Comment on this post"
                required
                value={formData.text}
                onChange={(e) => {
                  onChange(e);
                }}
              ></textarea>
              <input type="submit" class="btn btn-dark my-1" value="Submit" />
            </form>
          </div>

          <div class="comments">
            <div class="post bg-white p-1 my-1">
              <div>
                <a href="profile.html">
                  <img
                    class="round-img"
                    src="https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50?s=200"
                    alt=""
                  />
                  <h4>John Doe</h4>
                </a>
              </div>
              <div>
                <p class="my-1">
                  Lorem ipsum dolor sit amet consectetur adipisicing elit. Sint
                  possimus corporis sunt necessitatibus! Minus nesciunt soluta
                  suscipit nobis. Amet accusamus distinctio cupiditate
                  blanditiis dolor? Illo perferendis eveniet cum cupiditate
                  aliquam?
                </p>
                <p class="post-date">Posted on 04/16/2019</p>
              </div>
            </div>

            <div class="post bg-white p-1 my-1">
              <div>
                <a href="profile.html">
                  <img
                    class="round-img"
                    src="https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50?s=200"
                    alt=""
                  />
                  <h4>John Doe</h4>
                </a>
              </div>
              <div>
                <p class="my-1">
                  Lorem ipsum dolor sit amet consectetur adipisicing elit. Sint
                  possimus corporis sunt necessitatibus! Minus nesciunt soluta
                  suscipit nobis. Amet accusamus distinctio cupiditate
                  blanditiis dolor? Illo perferendis eveniet cum cupiditate
                  aliquam?
                </p>
                <p class="post-date">Posted on 04/16/2019</p>
                <button type="button" class="btn btn-danger">
                  <i class="fas fa-times"></i>
                </button>
              </div>
            </div>
          </div>
        </Fragment>
      ) : (
        <p>No Post Found</p>
      )}
    </Fragment>
  );
};

PostDetails.propTypes = {};

export default globalConnect()(PostDetails);
