import { SET_ALERT, REMOVE_ALERT } from "../action/types";

const initialState = [];
export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case SET_ALERT: {
      const pay = payload;
      setTimeout(() => state.filter((alert) => alert.id !== pay.id), 2000);
      return [...state, payload];
    }
    default: {
      return state;
    }
  }
}
