export const AlertHelper = (msg, alertType, props) => {
  const id = Math.floor(Math.random() * 100000);
  props.set_alert({
    id: id,
    msg: msg,
    alertType: alertType,
  });
  setTimeout(() => {
    props.remove_alert(id);
  }, 3000);
};
