import UID from "uuid";
import { REMOVE_ALERT, SET_ALERT } from "./types";

export const setAlert = (msg, alertType) => (dispatch) => {
  const id = UID.v4();
  dispatch({
    type: SET_ALERT,
    payload: { msg, alertType, id },
  });
};
